import React, { useState } from "react";
import { TextField, Button, Box } from '@mui/material';
import Card from "@mui/material/Card";
import { Grid } from "@mui/material";


const initialState = {
  name: '',
  email: '',
  message: '',
}

const MaterialFormComponent = (props) => {
  const [{ name, email, message }, setState] = useState(initialState)

  const handleChange = (e) => {
    const { name, value } = e.target
    setState((prevState) => ({ ...prevState, [name]: value }))
  }
  const clearState = () => setState({ ...initialState })

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(name, email, message)
  }
    return (
      <Card elevation={4} 
        sx={{width: "100%", height: "400px", padding: "10px", borderRadius: "5px", border: "2px solid green", background: "transparent"}}
      > 
            <form onSubmit={handleSubmit} >
              <Grid container sx={{ height: "80%", border: "2px solid blue", gap: "20px"}} alignItems="center" justifyContent="center" direction="column" >
                  <Grid item>
                      <TextField
                        sx={{width: "450px",  background: "white", borderRadius: "5PX", boxShadow: 5}}
                        id="name"
                        name="name"
                        label="Name"
                        type="text"
                        value={initialState.name}
                        onChange={handleChange}
                      />
                  </Grid>
                  <Grid item>
                      <TextField
                        sx={{color: "red", width: "450px",  background: "white", borderRadius: "5PX", boxShadow: 9}}
                        id="email"
                        name="email"
                        label="Email"
                        type="text"
                        value={initialState.email}
                        onChange={handleChange}
                        multiline
                      />
                  </Grid>

                  <Grid item>
                    <TextField
                      sx={{width: "450px",  background: "white", borderRadius: "5PX", boxShadow: 9}}
                      id="message"
                      name="message"
                      label="Message"
                      type="text"
                      value={initialState.message}
                      onChange={handleChange}
                      multiline
                      rows={5}
                      rowsMax={10}
                    />
                  </Grid>
              </Grid>

              <Grid container sx={{ height:"100%", border: "2px solid black"}} alignItems="center" direction="column" >
                <Grid item>
                    <Button variant="contained" color="primary" type="submit" style={{
                        backgroundColor: "green",
                        margin: "5px"
                    }}>
                        Send Message
                    </Button>
                </Grid>
                </Grid>

            </form>
      </Card>

    );
}
export default MaterialFormComponent;
